import React, { useState, useEffect } from "react";
import PostService from "../services/post.service";
import SAMPLEDATA from "../data/sample.json";

const Home = () => {
  const [searchText, setSearchText] = useState("");
  const [flickr, setFlickr] = useState([]);

  useEffect(() => {
    // PostService.getFlickr().then(
    //   (response) => {
    //     setFlickr(response.data);
    //   },
    //   (error) => {
    //     console.log(error);
    //   }
    // );
    console.log(SAMPLEDATA.items)
  }, []);
  function clickMe() {
    // alert("You clicked me!"+searchText);
    PostService.getFlickr(searchText).then(
      (response) => {
        setFlickr(response.data);
      },
      (error) => {
        console.log(error);
      }
    );
  }
  return (
    <div>
      <div>
      <input type="text"  placeholder="Search Flickr by Tags" onChange={(e) => setSearchText(e.target.value)}/><button type="button" className="btn btn-light" onClick={clickMe}>Search</button>
      </div>
      <section className="card-container">
        {flickr.items?.map((val, key) => {
            return (<div className="card" key={key}>
            <img src={val.media.m}/>  
            <p>{val.title}</p>  
            </div>);
        })}
      </section>
    </div>
  );
};

export default Home;
