export default function authHeader() {
  const user = JSON.parse(localStorage.getItem("user"));

  if (user && user.accessToken) {
    return { Authorization: 'Bearer ' + user.jwt };
    // return { "x-auth-token": user.accessToken };
  } else {
    return {};
  }
}
